<script>
$(function() {

    $(".toggle-confirmpassword").click(function() {
        $(this).toggleClass("fa-eye fa-eye-slash");
        var input = $($(this).attr("toggle"));
        if (input.attr("type") == "password") {
            input.attr("type", "text");
        } else {
            input.attr("type", "password");
        }
    });

    $("#loginForm").validate({
        rules: {
            email: {
                required: true,
                email: true,
            },
            password: {
                required: true
            }
        },
        messages: {
            email: {
                required: "Please enter email.",

            },
            password: {
                required: "Please enter password.",
            }
        },
        submitHandler: function(form) {
            console.log("Hi");
            form.submit();
        }
    });
    $.validator.addMethod('email', function(value) {
        return /^([A-Z|a-z|0-9](\.|_){0,1})+[A-Z|a-z|0-9]\@([A-Z|a-z|0-9])+((\.){0,1}[A-Z|a-z|0-9]){2}\.[a-z]{2,3}$/
            .test(
                value);
    })
    $('#password-field').keypress(function(e) {
        if (/[ ]/.test(String.fromCharCode(e.which)))
            return false;
    });
});

function allowAlphaNumericSpaceEmail(thisInput) {
    thisInput.value = thisInput.value.split(/[^a-zA-Z0-9-._@ ]/).join('');
}
</script>

